import React, { useState } from 'react';
import { ImageBackground, StyleSheet, View, Image, Text,Alert, Modal, TouchableHighlight, TextInput } from 'react-native';
import colors from '../config/colors';

function WelcomeScreen() {
    const [logModalVisible, setLogModalVisible] = useState(false);
    const [regModalVisible, setRegModalVisible] = useState(false);
    return (
        <ImageBackground 
        style={styles.background}
        source={require('../assets/background.jpg')}
        >
            <Modal
            style={{width: "90%", height: "70%"}}
            animationType="slide"
            transparent={true}
            visible={logModalVisible}
            onRequestClose={() => {Alert.alert("Modal has been closed.")}}
            >
        <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <TextInput style={styles.modalText}/>

            <TouchableHighlight
              style={styles.loginButton}
              onPress={() => {
                setLogModalVisible(false);
              }}
            >
              <Text style={styles.textStyle}>Hide Modal</Text>
            </TouchableHighlight>
          </View>
        </View>
      </Modal>
      <Modal
        animationType="slide"
        transparent={true}
        visible={regModalVisible}
        onRequestClose={() => {
          Alert.alert("Modal has been closed.");
        }}
      >
        <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <Text style={styles.modalText}>Hello World!</Text>

            <TouchableHighlight
              style={styles.registerButton}
              onPress={() => {
                setRegModalVisible(false);
              }}
            >
              <Text style={styles.textStyle}>Hide Modal</Text>
            </TouchableHighlight>
          </View>
        </View>
      </Modal>
            <View style={styles.logoContainer}>
            <Image style={styles.logo} source={require('../assets/logo_ct_rondnoir_blanc.png')}/>
            <Text>Sell What You Don't Need</Text>
            </View>
            <TouchableHighlight style={styles.loginButton} onPress={() => {setLogModalVisible(true)}}><Text>Login</Text></TouchableHighlight>
            <TouchableHighlight style={styles.registerButton} onPress={() => {setRegModalVisible(true)}}><Text>Sign in</Text></TouchableHighlight>
        </ImageBackground>
    );
}

const styles = StyleSheet.create({
    background: {
        flex: 1,
        justifyContent: "flex-end",
        alignItems: "center"
    },
    loginButton: {
        width: "100%",
        height: 70,
        backgroundColor: colors.secondary,
        justifyContent: "center",
        alignItems: "center"
    },
    registerButton: {
        width: "100%",
        height: 70,
        backgroundColor: colors.primary,
        justifyContent: "center",
        alignItems: "center"
    },
    logo:{
        width:100,
        height: 100,
    },
    logoContainer: {
        position: "absolute",
        top: 70,
        alignItems: "center"
    },
    centeredView: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        marginTop: 22,
      },
      modalView: {
        margin: 20,
        backgroundColor: "white",
        borderRadius: 20,
        padding: 35,
        alignItems: "center",
        shadowColor: "#000",
        shadowOffset: {
          width: 0,
          height: 2
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 5
      },
      textStyle: {
        color: "white",
        fontWeight: "bold",
        textAlign: "center"
      },
      modalText: {
        marginBottom: 15,
        textAlign: "center",
        borderBottomWidth: 1,
        borderBottomColor: "#000",
        width: "100%",
      }
})

export default WelcomeScreen;
